import React, {useEffect, useState} from "react";
import "../styles.css";
import {Link, Outlet} from "react-router-dom";
import {Button} from "react-bootstrap";
import {Col} from "reactstrap";


function CreateProfile(){

    const [errorMessages, setErrorMessages] = useState({});
    const [isSubmitted, setIsSubmitted] = useState(false);
    const [profileRegistration, setProfileRegistration] = useState({
        weight: "",
        height: "",
        birthdate: "",
        activityLevel: "",
        weightGoal: "",
        numberOfStepsGoal: "",
        regularUser: []
    });
    const [activityLevels, setActivityLevels] = useState( [] );
    const [checkedList, setCheckedList] = useState(new Array(activityLevels.length).fill(false));
    const [checkedState, setCheckedState] = useState(
        new Array(activityLevels.length).fill(false)
    );
    const[selectedLevel, setSelectedLevel] = useState("");

    useEffect(() => {
        fetch('http://localhost:8080/health-reset/activityLevel/index')
            .then((response) => response.json())
            .then((json) => {
                setActivityLevels(json);
            })
            .catch((error) => {
                console.log(error);
            });
        console.log(activityLevels)
        localStorage.setItem("activityLevels", JSON.stringify(activityLevels));
    }, []);

    const errors = {
        weight: "invalid weight",
        height: "invalid height",
    };

    const handleSubmit = (event) => {
        // Prevent page reload
        event.preventDefault();

        // axios
        //     .post('http://localhost:8080/assignment2/restaurant/create', profileRegistration)
        //     .then((response) => {
        //         console.info(response);
        //         if (response.data === false) {
        //             setErrorMessages({name: "name", message: errors.name});
        //             localStorage.removeItem("restaurant");
        //         } else {
        //             setIsSubmitted(true);
        //             localStorage.setItem("restaurant", JSON.stringify(profileRegistration));
        //             localStorage.setItem("restaurantName", JSON.stringify(profileRegistration.name));
        //         }
        //     })
        //     .catch((error) => {
        //         console.error("There was an error!", error.response.data.message)
        //     });

    };

    const handleInput = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setProfileRegistration({ ...profileRegistration, [name] : value,
            regularUser: JSON.parse(localStorage.getItem('regularUser'))});
    }

    function toggleOption(id, checked) {
        return activityLevels.map((id, activityLevel) =>
            id === id ? { ...activityLevel, checked } : activityLevel
        );
    }

    const changeList = (id, checked) => {
        console.log(id);
        const newCheckedList = toggleOption(id, checked);
        setCheckedList(newCheckedList);
    };

    const handleChange = e => {
        const target = e.target;
        if (target.checked) {
            setSelectedLevel(target.value);
        }
    };

    const renderErrorMessage = (name) =>
        name === errorMessages.name && (
            <div className="error">{errorMessages.message}</div>
        );

    const renderForm = (
        <div className="form">
            <form onSubmit = {handleSubmit}>
                <div className="input-container">
                    <label>Weight </label>
                    <input type="number"
                           value={profileRegistration.weight}
                           onChange={handleInput}
                           name="weight" required/>
                    {renderErrorMessage("weight")}
                </div>
                <div className="input-container">
                    <label>Height </label>
                    <input type="number"
                           value={profileRegistration.height}
                           onChange={handleInput}
                           name="height" required/>
                    {renderErrorMessage("height")}
                </div>
                <div className="input-container">
                    <label>Activity level: </label>
                        {activityLevels.map(({ id, activityLevel}, index) => (
                                <div key={activityLevel}>

                                    {/*    type="radio"*/}
                                    {/*    name={activityLevel}*/}
                                    {/*    value={activityLevel}*/}
                                    {/*    checked={checkedState[selectedLevel === {activityLevel}]}*/}
                                    {/*    onChange={handleChange}*/}

                                    <input
                                        type="radio"
                                        name={activityLevel}
                                        value={activityLevel}
                                        {...activityLevel("activityLevels", { required: true })}
                                        className="radio"
                                        defaultChecked={state.data.checked === "1"}
                                    />
                                    <label>{activityLevel}</label>
                                </div>
                            ))}
                </div>
                <div className="button-container">
                    <input type="submit"/>
                </div>
            </form>
        </div>
    );

    return (
        <div className="app">
            <div className="login-form" style={{backgroundColor: 'darkseagreen',}}>
                <div className="title">Complete your profile</div>
                {isSubmitted ?
                    <div>
                        <div>
                            Profile successfully created
                            <span>&nbsp;&nbsp;</span>
                        </div>
                        <span>&nbsp;&nbsp;</span>
                        <Link to="/RegularUserActions">
                            <span>&nbsp;&nbsp;</span>
                            <Button as={Col} variant="success">Go to user page</Button>
                        </Link>
                    </div>: renderForm}
                <Outlet />
            </div>
        </div>
    );
}

export default CreateProfile;
